use serde::{Deserialize, Serialize};

/// intermediate plext construct
pub type IntelPlext = (String, i64, PlextWrapper);
/// intermediate markup construct
pub type Markup = (String, MarkupElement);

/// COMM Tabs
#[derive(Debug, Serialize)]
#[serde(rename_all = "lowercase")]
pub enum Tab {
    /// COMM All Tabs
    All,
    /// COMM Faction Tabs
    Faction,
    /// COMM Alerts Tabs
    Alerts,
}

/// endpoint reponse root
#[derive(Debug, Deserialize)]
pub struct IntelResponse {
    /// "result" node
    pub result: Vec<IntelPlext>,
}

/// plext wrapper
#[derive(Debug, Deserialize)]
pub struct PlextWrapper {
    /// plext
    pub plext: Plext,
}

/// plext core object
#[derive(Debug, Deserialize)]
pub struct Plext {
    /// text description
    pub text: String,
    /// team
    pub team: String,
    /// markup
    pub markup: Vec<Markup>,
    /// type
    #[serde(rename = "plextType")]
    pub plext_type: String,
    /// category
    pub categories: u8,
}

/// markup object
#[derive(Debug, Deserialize)]
pub struct MarkupElement {
    /// text contents
    pub plain: String,
    /// name
    pub name: Option<String>,
    /// address
    pub address: Option<String>,
    /// latitude
    #[serde(rename = "latE6")]
    pub lat_e6: Option<i64>,
    /// longitude
    #[serde(rename = "lngE6")]
    pub lng_e6: Option<i64>,
    /// team
    pub team: Option<String>,
}

